sys_script_include.script:
var CaptureEfforts = Class.create();
CaptureEfforts.prototype = {
    initialize: function() {
    },

    punchIn: function() {
        var punchInField = this.getParameter('sysparm_punch_in_field');
        if (punchInField.getValue() == '') {
            var now = new GlideDateTime();
            punchInField.setValue(now);
        }
    },

    punchOut: function() {
        var punchOutField = this.getParameter('sysparm_punch_out_field');
        if (punchOutField.getValue() == '') {
            var now = new GlideDateTime();
            punchOutField.setValue(now);
        }
    },

    type: 'CaptureEfforts'
};
